<?phpclass Flyf_Database_Connection {	private static $instance;	private $hostname;	private $username;	private $password;	private $database;	private $charset;	private $pdo;	private $stmt;	private function __construct() {		$this->hostname = 'localhost';		$this->username = 'signifly';		$this->password = 'abcd1234';		$this->database = 'signifly';		$this->charset = 'utf8';	}	public static function getInstance() {		if (!self::$instance) {			self::$instance = new Flyf_Database_Connection();		}				return self::$instance;	}	public function connect() {		if (!$this->pdo) {			$this->pdo = new PDO(				'mysql:host='.$this->hostname.';dbname='.$this->database.';', 				$this->username, 				$this->password, 				array(					PDO::MYSQL_ATTR_INIT_COMMAND => 'SET NAMES '.$this->charset				)			);			$this->pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);		}	}	public function disconnect() {		$this->pdo = null;		$this->stmt = null;	}	public function prepare($query) {		$this->stmt = $this->pdo->prepare($query);	}	public function execute($fetch = true) {		// TODO		try {			$this->stmt->execute();			return array(				'result' => $fetch ? $this->stmt->fetchAll(PDO::FETCH_ASSOC) : array(),				'last_insert_id' => $this->pdo->lastInsertID(),				'row_count' => $this->stmt->rowCount(),			);		} catch (PDOException $e) {			throw new PDOException($e);		}	}	public function bind($params) {		foreach ($params as $key => $value) {			$this->stmt->bindParam($key, $value);		}	}}?>